document.addEventListener("DOMContentLoaded",(function(){const yearSelect=document.querySelectorAll("#year-select"),currentYear=(new Date).getFullYear();yearSelect.forEach((select=>{for(let year=currentYear;year>=1940;year--){const option=document.createElement("option");option.value=year,option.textContent=year,select.appendChild(option)}}));document.querySelectorAll("#day-select").forEach((select=>{for(let day=1;day<=31;day++){const option=document.createElement("option");option.value=day,option.textContent=day,select.appendChild(option)}}))}));