const statsData={yesterday:{registered:900,paidFull:300,paidPartial:200,unpaid:400},today:{registered:1808,paidFull:488,paidPartial:379,unpaid:941},week:{registered:5e3,paidFull:1500,paidPartial:1e3,unpaid:2500},month:{registered:2e4,paidFull:8e3,paidPartial:5e3,unpaid:7e3},year:{registered:24e4,paidFull:1e5,paidPartial:7e4,unpaid:7e4},custom:{registered:0,paidFull:0,paidPartial:0,unpaid:0}},datepicker=flatpickr("#custom-period",{mode:"range",dateFormat:"d.m.Y",locale:"ru",onClose:selectedDates=>{2===selectedDates.length&&(console.log(`Выбран период: с ${selectedDates[0]} по ${selectedDates[1]}`),updateStats("custom"))}});function updateStats(period){const{registered:registered,paidFull:paidFull,paidPartial:paidPartial,unpaid:unpaid}=statsData[period];document.getElementById("registered").textContent=registered,document.getElementById("paid-full").textContent=paidFull,document.getElementById("paid-partial").textContent=paidPartial,document.getElementById("unpaid").textContent=unpaid}document.querySelectorAll("input[name='period']").forEach((radio=>{radio.addEventListener("change",(event=>{const period=event.target.value;"custom"===period?document.getElementById("datepicker-container").style.display="block":(document.getElementById("datepicker-container").style.display="none",updateStats(period))}))}));