document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".search-btn"),t=document.querySelector(".search-input"),c=document.querySelectorAll(".header .link");e.addEventListener("click",(function(e){e.preventDefault(),c.forEach(e=>{e.classList.toggle("hidden")}),t.classList.toggle("active"),t.classList.contains("active")?t.focus():t.blur()}))}));